            {
                name:"css",
                img:""
            },
            {
                name:"JavaScript",
                img:""
            },
            {
                name:"ReactJs",
                img:""
            },
            {
                name:"NodeJs",
                img:""
            },
            {
                name:"ExpressJs"
            },
            {
                name:"Full Projects"
            }],


    }
const Course = () => {
    const [params , setParams] = useSearchParams();
    let coursedata = [];
    switch(params.get("c")) {
        case "Web":
            coursedata = data.Web;
          break;
        case "Ml":
            coursedata = data.Ml;
          break;
        default:
          // code block
      }   
  return (
    <div className={styles.corsecont}>
        {coursedata.map((e)=><CourseCard img={e.img} name={e.name}/>)}
    </div>
  )
}

export default Course
